pub fn get_bls12_381_curve_dbl_test_data(
    index: usize,
) -> Option<([u64; ARITH_EQ_384_U64S_DOUBLE], [u64; ARITH_EQ_384_U64S_DOUBLE])> {
    if let Some(sdata) = get_bls12_381_curve_dbl_test_str_data(index) {
        let bdata =
            str_test_data::<4, ARITH_EQ_384_U64S_DOUBLE>(index, "bls12_381_curve_dbl_test", sdata);
        Some((bdata[0], bdata[1]))
    } else {
        None
    }
}

